using AutoMapper;using MediatR;using ReservaHotel.Application.Amenities.Queries;using ReservaHotel.Application.Interfaces.General;using ReservaHotel.Domain.Entities;using ReservaHotel.Domain.Entities.Base;namespace ReservaHotel.Application.Amenities.Handlers{ public class GetAmenitiesHandler: IRequestHandler<GetAmenitiesQuery, CustomWebResponse>{ private readonly IReadRepository<Amenity> _repo; private readonly IMapper _mapper; public GetAmenitiesHandler(IReadRepository<Amenity> repo, IMapper mapper){ _repo=repo; _mapper=mapper;} public async Task<CustomWebResponse> Handle(GetAmenitiesQuery request, CancellationToken ct){ var list= await _repo.ListAsync(ct); var dto=_mapper.Map<List<Application.Common.Dtos.AmenityDto>>(list); return new CustomWebResponse{ ResponseBody=dto}; } } }