using AutoMapper;using MediatR;using ReservaHotel.Application.CustomerInteractions.Queries;using ReservaHotel.Application.CustomerInteractions.Specifications;using ReservaHotel.Application.Interfaces.General;using ReservaHotel.Domain.Entities;using ReservaHotel.Domain.Entities.Base;using System.Net;namespace ReservaHotel.Application.CustomerInteractions.Handlers{ public class GetCustomerInteractionByIdHandler: IRequestHandler<GetCustomerInteractionByIdQuery, CustomWebResponse>{ private readonly IReadRepository<CustomerInteraction> _repo; private readonly IMapper _mapper; public GetCustomerInteractionByIdHandler(IReadRepository<CustomerInteraction> repo, IMapper mapper){ _repo=repo; _mapper=mapper;} public async Task<CustomWebResponse> Handle(GetCustomerInteractionByIdQuery request, CancellationToken ct){ var spec=new CustomerInteractionByIdSpec(request.Id); var entity= await _repo.FirstOrDefaultAsync(spec, ct); if(entity==null) return new CustomWebResponse(true){ StatusCode=HttpStatusCode.NotFound, Message="CustomerInteraction not found"}; return new CustomWebResponse{ ResponseBody=_mapper.Map<Application.Common.Dtos.CustomerInteractionDto>(entity)}; } } }