using AutoMapper;using MediatR;using ReservaHotel.Application.CustomerInteractions.Commands;using ReservaHotel.Application.CustomerInteractions.Specifications;using ReservaHotel.Application.Interfaces.General;using ReservaHotel.Domain.Entities;using ReservaHotel.Domain.Entities.Base;using System.Net;namespace ReservaHotel.Application.CustomerInteractions.Handlers{ public class UpdateCustomerInteractionHandler: IRequestHandler<UpdateCustomerInteractionCommand, CustomWebResponse>{ private readonly IRepository<CustomerInteraction> _repo; private readonly IMapper _mapper; public UpdateCustomerInteractionHandler(IRepository<CustomerInteraction> repo, IMapper mapper){ _repo=repo; _mapper=mapper;} public async Task<CustomWebResponse> Handle(UpdateCustomerInteractionCommand request, CancellationToken ct){ var spec=new CustomerInteractionByIdSpec(request.Id); var entity= await _repo.FirstOrDefaultAsync(spec, ct); if(entity==null) return new CustomWebResponse(true){ StatusCode=HttpStatusCode.NotFound, Message="CustomerInteraction not found"}; _mapper.Map(request.CustomerInteraction, entity); await _repo.UpdateAsync(entity, ct); return new CustomWebResponse{ ResponseBody=_mapper.Map<Application.Common.Dtos.CustomerInteractionDto>(entity)}; } } }