using AutoMapper;using MediatR;using ReservaHotel.Application.InvoiceItems.Queries;using ReservaHotel.Application.InvoiceItems.Specifications;using ReservaHotel.Application.Interfaces.General;using ReservaHotel.Domain.Entities;using ReservaHotel.Domain.Entities.Base;using System.Net;namespace ReservaHotel.Application.InvoiceItems.Handlers{ public class GetInvoiceItemByIdHandler: IRequestHandler<GetInvoiceItemByIdQuery, CustomWebResponse>{ private readonly IReadRepository<InvoiceItem> _repo; private readonly IMapper _mapper; public GetInvoiceItemByIdHandler(IReadRepository<InvoiceItem> repo, IMapper mapper){ _repo=repo; _mapper=mapper;} public async Task<CustomWebResponse> Handle(GetInvoiceItemByIdQuery request, CancellationToken ct){ var spec=new InvoiceItemByIdSpec(request.Id); var entity= await _repo.FirstOrDefaultAsync(spec, ct); if(entity==null) return new CustomWebResponse(true){ StatusCode=HttpStatusCode.NotFound, Message="InvoiceItem not found"}; return new CustomWebResponse{ ResponseBody=_mapper.Map<Application.Common.Dtos.InvoiceItemDto>(entity)}; } } }