using AutoMapper;using MediatR;using ReservaHotel.Application.Invoices.Commands;using ReservaHotel.Application.Interfaces.General;using ReservaHotel.Domain.Entities;using ReservaHotel.Domain.Entities.Base;namespace ReservaHotel.Application.Invoices.Handlers{ public class CreateInvoiceHandler: IRequestHandler<CreateInvoiceCommand, CustomWebResponse>{ private readonly IRepository<Invoice> _repo; private readonly IMapper _mapper; public CreateInvoiceHandler(IRepository<Invoice> repo, IMapper mapper){ _repo=repo; _mapper=mapper;} public async Task<CustomWebResponse> Handle(CreateInvoiceCommand request, CancellationToken ct){ var entity=_mapper.Map<Invoice>(request.Invoice); await _repo.AddAsync(entity, ct); return new CustomWebResponse{ ResponseBody=_mapper.Map<Application.Common.Dtos.InvoiceDto>(entity)}; } } }