using AutoMapper;using MediatR;using ReservaHotel.Application.Locations.Queries;using ReservaHotel.Application.Interfaces.General;using ReservaHotel.Domain.Entities;using ReservaHotel.Domain.Entities.Base;namespace ReservaHotel.Application.Locations.Handlers{ public class GetLocationsHandler: IRequestHandler<GetLocationsQuery, CustomWebResponse>{ private readonly IReadRepository<Location> _repo; private readonly IMapper _mapper; public GetLocationsHandler(IReadRepository<Location> repo, IMapper mapper){ _repo=repo; _mapper=mapper;} public async Task<CustomWebResponse> Handle(GetLocationsQuery request, CancellationToken ct){ var list= await _repo.ListAsync(ct); var dto=_mapper.Map<List<Application.Common.Dtos.LocationDto>>(list); return new CustomWebResponse{ ResponseBody=dto}; } } }