using AutoMapper;using MediatR;using ReservaHotel.Application.StatusBookings.Queries;using ReservaHotel.Application.Interfaces.General;using ReservaHotel.Domain.Entities;using ReservaHotel.Domain.Entities.Base;namespace ReservaHotel.Application.StatusBookings.Handlers{ public class GetStatusBookingsHandler: IRequestHandler<GetStatusBookingsQuery, CustomWebResponse>{ private readonly IReadRepository<StatusBooking> _repo; private readonly IMapper _mapper; public GetStatusBookingsHandler(IReadRepository<StatusBooking> repo, IMapper mapper){ _repo=repo; _mapper=mapper;} public async Task<CustomWebResponse> Handle(GetStatusBookingsQuery request, CancellationToken ct){ var list= await _repo.ListAsync(ct); var dto=_mapper.Map<List<Application.Common.Dtos.StatusBookingDto>>(list); return new CustomWebResponse{ ResponseBody=dto}; } } }