using AutoMapper;using MediatR;using ReservaHotel.Application.Users.Commands;using ReservaHotel.Application.Interfaces.General;using ReservaHotel.Domain.Entities;using ReservaHotel.Domain.Entities.Base;namespace ReservaHotel.Application.Users.Handlers{ public class CreateUserHandler: IRequestHandler<CreateUserCommand, CustomWebResponse>{ private readonly IRepository<User> _repo; private readonly IMapper _mapper; public CreateUserHandler(IRepository<User> repo, IMapper mapper){ _repo=repo; _mapper=mapper;} public async Task<CustomWebResponse> Handle(CreateUserCommand request, CancellationToken ct){ var entity=_mapper.Map<User>(request.User); await _repo.AddAsync(entity, ct); return new CustomWebResponse{ ResponseBody=_mapper.Map<Application.Common.Dtos.UserDto>(entity)}; } } }