using AutoMapper;using MediatR;using ReservaHotel.Application.Users.Queries;using ReservaHotel.Application.Users.Specifications;using ReservaHotel.Application.Interfaces.General;using ReservaHotel.Domain.Entities;using ReservaHotel.Domain.Entities.Base;using System.Net;namespace ReservaHotel.Application.Users.Handlers{ public class GetUserByIdHandler: IRequestHandler<GetUserByIdQuery, CustomWebResponse>{ private readonly IReadRepository<User> _repo; private readonly IMapper _mapper; public GetUserByIdHandler(IReadRepository<User> repo, IMapper mapper){ _repo=repo; _mapper=mapper;} public async Task<CustomWebResponse> Handle(GetUserByIdQuery request, CancellationToken ct){ var spec=new UserByIdSpec(request.Id); var entity= await _repo.FirstOrDefaultAsync(spec, ct); if(entity==null) return new CustomWebResponse(true){ StatusCode=HttpStatusCode.NotFound, Message="User not found"}; return new CustomWebResponse{ ResponseBody=_mapper.Map<Application.Common.Dtos.UserDto>(entity)}; } } }