using Common.Interfaces;using MediatR;using Microsoft.AspNetCore.Mvc;using ReservaHotel.Application.CustomerInteractions.Queries;using ReservaHotel.Application.CustomerInteractions.Commands;using ReservaHotel.Application.Common.Dtos;using System;namespace ReservaHotel.Presentacion.Controllers.Rest{ [ApiController][Route("customer-interaction")] public class CustomerInteractionController: ControllerBase{ private readonly IWebTools _webTools; private readonly IMediator _mediator; public CustomerInteractionController(IWebTools webTools, IMediator mediator){ _webTools=webTools; _mediator=mediator;} [HttpGet] public async Task<IActionResult> GetAll(CancellationToken ct){ var response= await _mediator.Send(new GetCustomerInteractionsQuery(), ct); return _webTools.CustomResponse(response);} [HttpGet("{id}")] public async Task<IActionResult> Get(Guid id, CancellationToken ct){ var response= await _mediator.Send(new GetCustomerInteractionByIdQuery(id), ct); return _webTools.CustomResponse(response);} [HttpPost] public async Task<IActionResult> Post([FromBody] CustomerInteractionDto dto, CancellationToken ct){ var response= await _mediator.Send(new CreateCustomerInteractionCommand(dto), ct); return _webTools.CustomResponse(response);} [HttpPut("{id}")] public async Task<IActionResult> Put(Guid id, [FromBody] CustomerInteractionDto dto, CancellationToken ct){ dto.Id=id; var response= await _mediator.Send(new UpdateCustomerInteractionCommand(id, dto), ct); return _webTools.CustomResponse(response);} [HttpDelete("{id}")] public async Task<IActionResult> Delete(Guid id, CancellationToken ct){ var response= await _mediator.Send(new DeleteCustomerInteractionCommand(id), ct); return _webTools.CustomResponse(response);} } }