using Common.Interfaces;using MediatR;using Microsoft.AspNetCore.Mvc;using ReservaHotel.Application.Employees.Queries;namespace ReservaHotel.Presentacion.Controllers.Rest{ [ApiController][Route("employee")] public class EmployeeController: ControllerBase{ private readonly IWebTools _webTools; private readonly IMediator _mediator; public EmployeeController(IWebTools webTools, IMediator mediator){ _webTools=webTools; _mediator=mediator;} [HttpGet] public async Task<IActionResult> GetAll(CancellationToken ct){ var response= await _mediator.Send(new GetEmployeesQuery(), ct); return _webTools.CustomResponse(response);} } }