using Common.Interfaces;using MediatR;using Microsoft.AspNetCore.Mvc;using ReservaHotel.Application.Invoices.Queries;using ReservaHotel.Application.Invoices.Commands;using ReservaHotel.Application.Common.Dtos;using System;namespace ReservaHotel.Presentacion.Controllers.Rest{ [ApiController][Route("invoice")] public class InvoiceController: ControllerBase{ private readonly IWebTools _webTools; private readonly IMediator _mediator; public InvoiceController(IWebTools webTools, IMediator mediator){ _webTools=webTools; _mediator=mediator;} [HttpGet] public async Task<IActionResult> GetAll(CancellationToken ct){ var response= await _mediator.Send(new GetInvoicesQuery(), ct); return _webTools.CustomResponse(response);} [HttpGet("{id}")] public async Task<IActionResult> Get(Guid id, CancellationToken ct){ var response= await _mediator.Send(new GetInvoiceByIdQuery(id), ct); return _webTools.CustomResponse(response);} [HttpPost] public async Task<IActionResult> Post([FromBody] InvoiceDto dto, CancellationToken ct){ var response= await _mediator.Send(new CreateInvoiceCommand(dto), ct); return _webTools.CustomResponse(response);} [HttpPut("{id}")] public async Task<IActionResult> Put(Guid id, [FromBody] InvoiceDto dto, CancellationToken ct){ dto.Id=id; var response= await _mediator.Send(new UpdateInvoiceCommand(id, dto), ct); return _webTools.CustomResponse(response);} [HttpDelete("{id}")] public async Task<IActionResult> Delete(Guid id, CancellationToken ct){ var response= await _mediator.Send(new DeleteInvoiceCommand(id), ct); return _webTools.CustomResponse(response);} } }