using Common.Interfaces;using MediatR;using Microsoft.AspNetCore.Mvc;using ReservaHotel.Application.Locations.Queries;namespace ReservaHotel.Presentacion.Controllers.Rest{ [ApiController][Route("location")] public class LocationController: ControllerBase{ private readonly IWebTools _webTools; private readonly IMediator _mediator; public LocationController(IWebTools webTools, IMediator mediator){ _webTools=webTools; _mediator=mediator;} [HttpGet] public async Task<IActionResult> GetAll(CancellationToken ct){ var response= await _mediator.Send(new GetLocationsQuery(), ct); return _webTools.CustomResponse(response);} } }