using Common.Interfaces;using MediatR;using Microsoft.AspNetCore.Mvc;using ReservaHotel.Application.StatusBookings.Queries;namespace ReservaHotel.Presentacion.Controllers.Rest{ [ApiController][Route("status-booking")] public class StatusBookingController: ControllerBase{ private readonly IWebTools _webTools; private readonly IMediator _mediator; public StatusBookingController(IWebTools webTools, IMediator mediator){ _webTools=webTools; _mediator=mediator;} [HttpGet] public async Task<IActionResult> GetAll(CancellationToken ct){ var response= await _mediator.Send(new GetStatusBookingsQuery(), ct); return _webTools.CustomResponse(response);} } }