using Common.Interfaces;using MediatR;using Microsoft.AspNetCore.Mvc;using ReservaHotel.Application.TypeRooms.Queries;namespace ReservaHotel.Presentacion.Controllers.Rest{ [ApiController][Route("type-room")] public class TypeRoomController: ControllerBase{ private readonly IWebTools _webTools; private readonly IMediator _mediator; public TypeRoomController(IWebTools webTools, IMediator mediator){ _webTools=webTools; _mediator=mediator;} [HttpGet] public async Task<IActionResult> GetAll(CancellationToken ct){ var response= await _mediator.Send(new GetTypeRoomsQuery(), ct); return _webTools.CustomResponse(response);} } }